/* eslint-disable no-unused-vars */
/* eslint-disable no-param-reassign */
import {
  createSlice,
  createAsyncThunk,
} from '@reduxjs/toolkit';

import authService from 'services/authService';

const initialState = {
  status: 'idle',
  token: null,
  user: {},
};

// Thunk functions
export const attemptAuth = createAsyncThunk('auth/attemptAuth', async ({username, password, twoFactorAuthToken}) => {
  console.log(`[authSlice] attemptAuth: ${{ username, password, twoFactorAuthToken }}`);
  const response = await authService.getAuthToken(username, password, twoFactorAuthToken);
  return response.data;
});

export const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
  },
  extraReducers: (builder) => {
    builder
      .addCase(attemptAuth.pending, (state, action) => {
        state.status = 'loading';
      })
      .addCase(attemptAuth.fulfilled, (state, action) => {
        const {
          success,
          error,
          token,
          username,
        } = action.payload;

        if (success === false) {
          console.log(`failed to login user ${error}`);
          return;
        }
        state.token = token;
        state.user.username = username;
        state.status = 'idle';
      });
  },
});

// const isAuthenticated = () => {

// }

// Action creators are generated for each case reducer function
// export const { updateAuthState } = authSlice.actions;

export default authSlice.reducer;
