/* eslint-disable no-param-reassign */
import { createSlice } from '@reduxjs/toolkit';
import devHelpers from 'utils/devHelpers';

const initialState = {
  applicantsData: [
    ...devHelpers.getFakeApplicantsData({ num: 10 }),
  ],
};

export const applicantsSlice = createSlice({
  name: 'applicants',
  initialState,
  reducers: {
    updateApplicantsData(state, action) {
      const applicants = action.payload;
      state.applicants.applicantsData = [
        ...applicants,
      ];
    },
  },
});

// Action creators are generated for each case reducer function
export const { updateApplicantsData } = applicantsSlice.actions;

export default applicantsSlice.reducer;
