/* eslint-disable no-param-reassign */
import { createSlice } from '@reduxjs/toolkit';
import devHelpers from 'utils/devHelpers';


export const applicantsSlice = createSlice({
  name: 'applicants',
  initialState: {
    applicants: [...Array(10).keys()].map(() => (devHelpers.getFakeApplicantData)),
  },
  reducers: {
    updateApplicants(state, action) {
      state.applicants = [
        ...action.payload,
      ];
    },
  },
});

// Action creators are generated for each case reducer function
export const { updateApplicants } = applicantsSlice.actions;

export default applicantsSlice.reducer;
