/* eslint-disable no-param-reassign */
import { createSlice } from '@reduxjs/toolkit';

const initialState = [
  {
    id: 0,
    message: 'succesfully logged in user',
    color: 'black',
  }
];

export const alertsSlice = createSlice({
  name: 'alerts',
  initialState,
  reducers: {
    addAlert(state, action) {
      const {
        message,
        color,
      } = action.payload;
      const id = initialState.length + 1;
      state.push({
        id,
        message,
        color,
      });
    },
    clearAlert(state, action) {
      const alertsId = action.payload;
      state = state.filter(({ id }) => id !== alertsId);
    },
  },
});

const getAllAlerts = (state) => state.alerts;

export {
  getAllAlerts,
};

export const { addAlert, clearAlert } = alertsSlice.actions;

export default alertsSlice.reducer;
