/* eslint-disable react/prop-types */
import React from 'react';
import './Alerts.css';
import { clearAlert, getAllAlerts } from 'state-management/slices/alertsSlice';
import { useDispatch, useSelector } from 'react-redux';
import ALERT_TYPES from './ALERT_TYPES';
// import PropTypes from 'prop-types';

const Alerts = () => {
  const alerts = useSelector(getAllAlerts);
  return (
    <div className="alerts">
      {
        alerts.map((alert) => (
          <Alert
            id={alert.id}
            message={alert.message}
            type={alert.type}
          />
        ))
      }
    </div>
  );
};

const getAlertBackground = (alertType) => ({
  [ALERT_TYPES.SUCCESS]: 'has-background-success',
  [ALERT_TYPES.INFO]: 'has-background-info',
  [ALERT_TYPES.WARNING]: 'has-background-warning',
  [ALERT_TYPES.DANGER]: 'has-background-danger',
}[alertType] || '');

const Alert = ({ id, message, type }) => {
  const dispatch = useDispatch();
  const bulmaBackground = getAlertBackground(type);

  return (
    <div id={id} className={`alert ${bulmaBackground}`}>
      {message}
      <button type="button" className="button is-ghost" onClick={() => dispatch(clearAlert(id))} style={{ float: 'right' }}>
        <span className="alert-close icon is-large">
          <i className="fas fa-times" />
        </span>
      </button>
    </div>
  );
};

Alert.propTypes = {
};

export default Alerts;
