/* eslint-disable semi */
/* eslint-disable no-param-reassign */
import { createSlice, createAsyncThunk} from '@reduxjs/toolkit';
import devHelpers from 'utils/devHelpers';
import applicantsService from 'services/applicants';

const initialState = {
  applicantsData: [
    ...devHelpers.getFakeApplicantsData({ num: 10 }),
  ],
};

export const applicantsSlice = createSlice({
  name: 'applicants',
  initialState,
  reducers: {
    updateApplicantsData(state, action) {
      const applicantsData = action.payload;
      state.applicantsData = [
        ...applicantsData,
      ];
    },
  },
});

export const fetchAllApplicants = createAsyncThunk('applicants/fetchApplicants', async () => {
  const response = await applicantsService.getAllApplicants
  return response.todos
});

// Action creators are generated for each case reducer function
export const { updateApplicantsData } = applicantsSlice.actions;

export default applicantsSlice.reducer;
