import React from 'react';
import './Alerts.css';
import { getAllAlerts } from 'state-management/slices/alertsSlice';
import { useSelector } from 'react-redux';

const Alerts = () => {
  const alerts = useSelector(getAllAlerts);
  return (
    <div className="alerts">
      {
        alerts.map((alert) => (
          <Alert
            id={alert.id}
            message={alert.message}
          />
        ))
      }
    </div>
  );
};

const Alert = ({id, message }) => {
  return (
    <div id={id} className="alert has-background-success">
        {message}
      <span style={{ float: 'right' }}>‚ùå</span>
    </div>
  )
}

export default Alerts;
