/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/* eslint-disable no-param-reassign */
import {
  createSlice,
  createAsyncThunk,
} from '@reduxjs/toolkit';

import authService from 'services/authService';

const initialState = {
  status: 'idle',
  token: null,
  user: {
    username: null,
  },
};

// Thunk functions
export const attemptAuth = createAsyncThunk('auth/attemptAuth', async ({ username, password, twoFactorAuthToken }) => {
  console.log(`[authSlice] attemptAuth: ${{ username, password, twoFactorAuthToken }}`);
  const {
    success,
    error,
    token,
    _username,
  } = await authService.getAuthToken(username, password, twoFactorAuthToken);
  console.log({
    success, error, token, username,
  });

  if (success === false) {
    console.log(`failed to login user ${error}`);
    alert('failed to login user');
  }
  if (success === true) {
    localStorage.setItem('X-Raya-Token', token);
    alert('successfully logged in user');
  }

  return {
    token,
    _username,
  };
});

export const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    reset: (state, action) => {

    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(attemptAuth.pending, (state, action) => {
        state.status = 'loading';
      })
      .addCase(attemptAuth.fulfilled, (state, action) => {
        console.log('state is fulfilled', action.payload);
        const {
          token,
          username,
        } = action.payload;

        state.token = token;
        state.user.username = username;
        state.status = 'idle';
      });
  },
});

// const isAuthenticated = () => {

// }

// Action creators are generated for each case reducer function
// export const { updateAuthState } = authSlice.actions;

export default authSlice.reducer;
