/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable jsx-a11y/no-noninteractive-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import React, { useState } from 'react';
import PropTypes from 'prop-types';
import faker from 'faker';
import _ from 'lodash';

function getRandomItem(items) {
  return items[Math.floor(Math.random() * items.length)];
}

const getApplicantData = ({ userId }) => {
  if (userId == null) {
    console.error('[ERROR] func getApplicantData: the fuck is this user id null for');
  }
  return {
    userId,
    name: faker.name.findName(),
    instagramId: faker.datatype.uuid(),
    instagramVisibility: getRandomItem(['public', 'private']),
    isInstagramVerified: faker.datatype.boolean().toString(),
    birthDate: faker.date.past(),
    displayGender: 'straight',
  };
};

const calculateAge = (dateOfBirth) => {
  if (_.isEmpty(dateOfBirth)) {
    return 0;
  }
  const diffMS = Date.now() - dateOfBirth.getTime();
  const ageDt = new Date(diffMS);

  return Math.abs(ageDt.getUTCFullYear() - 1970);
};

const Applicants = () => {
  const applicantsSectionEnum = {
    SEARCH: 'SEARCH',
    TO_REVIEW: 'TO_REVIEW',
    FRIEND_PASS: 'FRIEND_PASS',
    ACCEPTED_PUSH_TO_RELEASE: 'ACCEPTED_PUSH_TO_RELEASE',
    GIA: 'GIA',
    WAITLISTED_PUSH_TO_RELEASE: 'WAITLISTED_PUSH_TO_RELEASE',
    ACCEPTED_ON_APP: 'ACCEPTED_ON_APP',
    WAITLISTED: 'WAITLISTED',
    BANNED: 'BANNED',
  };

  const [applicantsSection, setApplicantsSection] = useState(applicantsSectionEnum.SEARCH);
  const [applicantInfo, setApplicantInfo] = useState(getApplicantData({ userId: 1 }));

  return (
    <div>
      <ApplicantsTabBar
        applicantsSectionEnum={applicantsSectionEnum}
        applicantsSection={applicantsSection}
        setApplicantsSection={setApplicantsSection}
      />
      <ApplicantsTable
        applicantInfo={applicantInfo}
        setApplicantInfo={setApplicantInfo}
      />
      <ApplicantDetailedInfo
        // eslint-disable-next-line react/jsx-props-no-spreading
        {...applicantInfo}
        setApplicantInfo={setApplicantInfo}
      />
    </div>
  );
};

const ApplicantsTabBar = ({ applicantsSectionEnum, applicantsSection, setApplicantsSection }) => {
  const applicantsSectionOnClickHandler = (section) => {
    setApplicantsSection(applicantsSectionEnum[section]);
  };

  return (
    <div className="tabs is-boxed">
      <ul>
        {
              [
                { title: 'Search', section: applicantsSectionEnum.SEARCH },
                { title: 'To Review', section: applicantsSectionEnum.TO_REVIEW },
                { title: 'Friend Pass', section: applicantsSectionEnum.FRIEND_PASS },
                { title: 'Accepted: Push to Release', section: applicantsSectionEnum.ACCEPTED_PUSH_TO_RELEASE },
                { title: 'GIA', section: applicantsSectionEnum.GIA },
                { title: 'Waitlisted: Push to Release', section: applicantsSectionEnum.WAITLISTED_PUSH_TO_RELEASE },
                { title: 'Accepted: On App', section: applicantsSectionEnum.ACCEPTED_ON_APP },
                { title: 'Waitlisted', section: applicantsSectionEnum.WAITLISTED },
                { title: 'Banned', section: applicantsSectionEnum.BANNED },
              ].map((tabInfo) => (
                <li className={`${applicantsSection === tabInfo.section ? 'is-active' : ''}`}>
                  <a
                    role="tabpanel"
                    onClick={() => applicantsSectionOnClickHandler(tabInfo.section)}
                  >
                    {tabInfo.title}
                  </a>
                </li>
              ))
            }
      </ul>
    </div>
  );
};

ApplicantsTabBar.propTypes = {
  applicantsSectionEnum: PropTypes.shape({
    SEARCH: PropTypes.string.isRequired,
    TO_REVIEW: PropTypes.string.isRequired,
    FRIEND_PASS: PropTypes.string.isRequired,
    ACCEPTED_PUSH_TO_RELEASE: PropTypes.string.isRequired,
    GIA: PropTypes.string.isRequired,
    WAITLISTED_PUSH_TO_RELEASE: PropTypes.string.isRequired,
    ACCEPTED_ON_APP: PropTypes.string.isRequired,
    WAITLISTED: PropTypes.string.isRequired,
    BANNED: PropTypes.string.isRequired,
  }).isRequired,
  applicantsSection: PropTypes.string.isRequired,
  setApplicantsSection: PropTypes.func.isRequired,
};

const ApplicantsTable = ({ setApplicantInfo }) => {
  const applicantsData = [...Array(10).keys()].map(() => ({
    userId: faker.datatype.number(),
    name: faker.name.findName(),
    handle: faker.name.findName(),
    priorityQueue: faker.datatype.number(),
    score: faker.datatype.number(),
    applicationDate: faker.date.past().toDateString(),
  }));

  const applicantDataOnClickHandler = (applicantUserId) => {
    setApplicantInfo(getApplicantData(applicantUserId));
  };

  return (
    <div>
      <table className="table is-striped is-bordered is-hoverable is-fullwidth">
        <thead>
          <tr>
            <th>Name</th>
            <th>Handle</th>
            <th><abbr title="Prior. Q.">Priority Queue</abbr></th>
            <th><abbr title="Played">Score</abbr></th>
            <th><abbr title="Won">Application Date</abbr></th>
          </tr>
        </thead>
        <tbody>
          {
            applicantsData.map((applicantData) => (
              <tr onClick={() => applicantDataOnClickHandler(applicantData.userId)}>
                <td>{applicantData.name}</td>
                <td>{applicantData.handle}</td>
                <td>{applicantData.priorityQueue}</td>
                <td>{applicantData.score}</td>
                <td>{applicantData.applicationDate}</td>
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>
  );
};

ApplicantsTable.propTypes = {
  setApplicantInfo: PropTypes.func.isRequired,
};

const ApplicantDetailedInfo = ({
  name,
  instagramId,
  instagramVisibility,
  isInstagramVerified,
  birthDate,
  displayGender,
}) => {
  console.log('ApplicantDetailedInfo');
  return (
    <div className="box">
      <div>
        <span>Name:</span>
&nbsp;
        {name}
      </div>
      <div>
        <span>Instagram ID:</span>
&nbsp;
        {instagramId}
      </div>
      <div>
        <span>Instagram Visibility Settings:</span>
&nbsp;
        {instagramVisibility}
      </div>
      <div>
        <span>Instagram Verified?: </span>
        {isInstagramVerified}
      </div>
      <div>
        <span>Age: </span>
        {calculateAge(birthDate)}
      </div>
      <div>
        <span>Display Gender: </span>
        {displayGender}
      </div>
    </div>
  );
};

ApplicantDetailedInfo.propTypes = {
  name: PropTypes.string.isRequired,
  instagramId: PropTypes.string.isRequired,
  instagramVisibility: PropTypes.string.isRequired,
  isInstagramVerified: PropTypes.string.isRequired,
  birthDate: PropTypes.instanceOf(Date).isRequired,
  displayGender: PropTypes.string.isRequired,
};

export default Applicants;
