/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/* eslint-disable no-param-reassign */
import {
  createSlice,
  createAsyncThunk,
} from '@reduxjs/toolkit';
import { useSelector, useDispatch } from 'react-redux';
import authService from 'services/authService';

const initialState = {
  status: 'idle',
  isAuthenticated: false,
  user: {
    username: null,
  },
};

// Thunk functions
const login = createAsyncThunk('auth/login', async ({ username, password, twoFactorAuthToken }) => {
  console.log(`[authSlice] login: ${{ username, password, twoFactorAuthToken }}`);

  const {
    success,
    error,
    token,
    username: _username,
  } = await authService.getAuthToken(username, password, twoFactorAuthToken);
  console.log('[authSlice] login: getAuthToken response received', {
    success, error, token, _username,
  });

  if (success === false) {
    console.log(`failed to login user ${error}`);
    alert('failed to login user');
  }
  if (success === true) {
    localStorage.setItem('X-Raya-Token', token);
    alert('successfully logged in user');
  }

  return {
    isAuthenticated: success,
    username: _username,
  };
});

const logout = createAsyncThunk('auth/logout', async () => {
  console.log('[authSlice] logout');
  localStorage.removeItem('X-Raya-Token');
});

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    reset: {
      reducer(state, action) {
        state = initialState;
      },
    },
    logout: {
      reducer(state, payload) {
        try {
          state = initialState;
        } catch (error) {
          console.error(error);
        }
      },
      prepare() {
        localStorage.removeItem('X-Raya-Token');
      },
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(login.pending, (state, action) => {
        state.status = 'loading';
      })
      .addCase(login.fulfilled, (state, action) => {
        const {
          isAuthenticated,
          username,
        } = action.payload;

        state.isAuthenticated = isAuthenticated;
        state.user.username = username;
        state.status = 'idle';
      });
  },
});

const isLoggedIn = (state) => state.auth.isAuthenticated;

const getAuthToken = () => {
  const authToken = localStorage.get('X-Raya-Token');
  if (authToken != null) {
    return true;
  }
  return false;
};

export {
  isLoggedIn,
  getAuthToken,
  login,
  logout,
};

// Action creators are generated for each case reducer function
export const {
  reset,
} = authSlice.actions;

export default authSlice.reducer;
