/* eslint-disable react/prop-types */
import React from 'react';
import './Alerts.css';
import { clearAlert, getAllAlerts } from 'state-management/slices/alertsSlice';
import { useDispatch, useSelector } from 'react-redux';
// import PropTypes from 'prop-types';

const Alerts = () => {
  const alerts = useSelector(getAllAlerts);
  console.log(alerts);
  return (
    <div className="alerts">
      {
        alerts.map((alert) => (
          <Alert
            id={alert.id}
            message={alert.message}
          />
        ))
      }
    </div>
  );
};

const Alert = ({ id, message }) => {
  const dispatch = useDispatch();

  return (
    <div id={id} className="alert has-background-success">
      {message}
      <button className="button" onClick={e => dispatch(clearAlert(id))} style={{ float: 'right' }}>
        ‚ùå
      </button>
    </div>
  );
};

Alert.propTypes = {
};

export default Alerts;
